mapper.select:
select count(*)
from cart
where user_id = #{userId} and product_id = #{productId}

mapper.update:
update cart
set	amount = nvl(amount, 0) + #{amount}			
where user_id = #{userId} and product_id = #{productId}

mapper.delete:
delete 
from cart
where user_id = #{userId} and product_id = #{productId}
