mapper.select:
SELECT c.user_id, c.product_id, c.amount, c.seller_id,
p.thumbnail, p.name as product_Name, p.price - p.price * (p.discount / 100) as discount_price, (select company.name from company, users where company.id = users.company_id and users.id = p.seller_id) as company_name, p.delivery_price,
p.price, to_char(c.amount, 'FM999,999,999') fmt_amount, to_char(p.price, 'FM999,999,999') fmt_price, p.discount || '%' fmt_discount, case p.delivery_price when 0 then '무료배송' else to_char(p.delivery_price, 'FM999,999,999') end fmt_delivery_price, to_char(p.price - p.price * (p.discount / 100), 'FM999,999,999') fmt_discount_price FROM cart c JOIN product p ON c.product_id = p.id where c.user_id = #{userId}

SELECT COUNT(*) AS count
FROM cart
WHERE user_id = #{userId}

select count(*)
from cart
where user_id = #{userId} and product_id = #{productId}

mapper.update:
update cart
set	amount = nvl(amount, 0) + #{amount}
where user_id = #{userId} and product_id = #{productId}

mapper.delete:
delete 
from cart
where

		user_id = #{userId}


		and product_id = #{productId}

mapper.select:
select product_id
from cart
where user_id = #{userId}
