mapper.select:
select count(*) from product where 1 = 1
and name like '%' || ${name} || '%'
and category_id = #{categoryId}

mapper.update:
update product
set	modify_date = sysdate			

		, name = #{name}


		, category_id = #{categoryId}


		, amount = #{amount}


		, price = #{price}


		, discount = #{discount}


		, delivery_price = #{deliveryPrice}


		, use_yn = #{useYn}


		, contents = #{contents}


		, thumbnail = #{thumbnail}

where id = #{id}
